Properties.ProtectedString:
--[[ alexnewtron 2014 ]]--
script.Parent:WaitForChild("Handle");
local gearConfig = {
	gear = script.Parent,
	equipped = false,
	check = true,
	debris = game:GetService("Debris"),
	anims = {
		script.Parent:WaitForChild("Slash2Anim"),
		script.Parent:WaitForChild("Slash3Anim"),
		script.Parent:WaitForChild("Slash4Anim")
	},
	sounds = {
		["swing"] = script.Parent.Handle:WaitForChild("Swing"),
		["hit"] = script.Parent.Handle:WaitForChild("Hit"),
	},
	readyAnims = {},
	pieces = {},
	force = 0.01,
	spacing = 0.55,
	push = -1000
};
function gearConfig.buildWeapon()
	local t = 11;
	for i=1, t do
		local p = Instance.new("Part", gearConfig.gear.Handle);
		p.Name = "Part"..t;
		p.Size = Vector3.new(0.51,0.57,0.63);
		p.CanCollide = false;
		p.Locked = true;
		p.Anchored = false;		
		local m = Instance.new("SpecialMesh", p);
		m.MeshId = "http://www.roblox.com/asset/?id=163291701";
		m.TextureId = "http://www.roblox.com/asset/?id=162383840";		
		local w = Instance.new("Weld", p);
		if (#gearConfig.pieces == 0) then
			w.Part0 = gearConfig.gear.Handle;
		else
			w.Part0 = gearConfig.pieces[#gearConfig.pieces];
		end
		w.Part1 = p;
		w.C0 = CFrame.new(0,0,gearConfig.spacing)*CFrame.Angles(gearConfig.force,0,0);
		w.C1 = CFrame.new(0,0,0)*CFrame.Angles(0,0,0);
		table.insert(gearConfig.pieces, p);
		p.Touched:connect(gearConfig.humanHit);
	end
	gearConfig.wiggle();
end
function gearConfig.wiggle()
	for i=1, #gearConfig.pieces do
		local c = gearConfig.pieces[i];
		if (c ~= nil) then
			local w = c:findFirstChild("Weld");
			if (w and w ~= nil) then
				w.C0 = CFrame.new(0,0,gearConfig.spacing)*CFrame.Angles(gearConfig.force,0,0);
			end
			wait();
		else
			break;
		end
	end
	return;
end

function gearConfig.humanHit(hit)
	if hit.Parent~=game.Players.LocalPlayer.Character and hit.Name~="Handle" and hit.Name~="Effect" then
		local h=hit.Parent:FindFirstChild("Humanoid");
		local t=hit.Parent:FindFirstChild("Torso");
		if h~=nil and t~=nil and h.Health>0 then
			if (h:findFirstChild("hit-deb") == nil and gearConfig.check == false and t.Parent:findFirstChild("ForceField") == nil) then
				-- We found a foe.. what shall we do with them?
				local pos = gearConfig.gear.Handle.Position;
				local dist = Vector3.new(pos.x - t.Position.x, pos.y - t.Position.y, pos.z - t.Position.z);
				local bodyForce = Instance.new("BodyForce")
				bodyForce.force = dist.unit * t:GetMass() * gearConfig.push;
				bodyForce.Parent = t;
				gearConfig.debris:AddItem(bodyForce, 0.5);
				h.Sit = true;
				gearConfig.sounds["hit"]:Play();				
				local c = Instance.new("BoolValue", h);
				c.Name = "hit-deb";				
				gearConfig.debris:AddItem(c, 3);	
			end
		end
	end
end

gearConfig.gear.Unequipped:connect(function()
	gearConfig.equipped = false;
	for i=1, #gearConfig.readyAnims do
		gearConfig.readyAnims[i]:Stop();
	end
	for i=1, #gearConfig.pieces do
		gearConfig.pieces[i]:Destroy();
	end
	gearConfig.pieces = {};
	gearConfig.gear.Handle.Mesh.MeshId = "http://www.roblox.com/asset/?id=162383812";
end);
gearConfig.gear.Equipped:connect(function(m)
	gearConfig.equipped = true;
	gearConfig.gear.Handle.Mesh.MeshId = "http://www.roblox.com/asset/?id=163291774";
	if (m ~= nil) then
		local human = script.Parent.Parent:findFirstChild("Humanoid");
		local torso = script.Parent.Parent:findFirstChild("Torso");
		gearConfig.readyAnims = {};
		for i=1, #gearConfig.anims do
			table.insert(gearConfig.readyAnims, human:LoadAnimation(gearConfig.anims[i]));
		end
		gearConfig.buildWeapon();
		m.Button1Down:connect(function()
			if (gearConfig.check and human.Health > 0) then
				gearConfig.check = false;
				local i = math.random(1,3);
				gearConfig.readyAnims[i]:Play();
				gearConfig.sounds["swing"]:Play();
				if (i == 1 or i == 2) then
					gearConfig.force = 0.15;
				elseif (i == 3) then
					gearConfig.force = -0.2;
				end
				gearConfig.wiggle(); wait(0.5);
				gearConfig.force = 0.01;
				gearConfig.wiggle();
				gearConfig.check = true;
			end
		end);
	end
end);
