--[[ alexnewtron 2014 ]]--
local c = {
	gear = script.Parent,
	sword = script.Parent:WaitForChild("Handle"),
	r=game:service("RunService"),
	debris=game:GetService("Debris"),
	anims={"RightSlash","LeftSlash","OverHeadSwing"},
	taunting = script.Parent:WaitForChild("Taunting"),
	sound = script.Parent.Handle:WaitForChild("Slash1"),
	c = {
		swingrate=1.1,
		damage=0,
		slashdamage=9,
		swingdamage=14,
		basedamage=0,
	}
}
c.taunting.Value=false;
function c.tagHumanoid(humanoid,player)
	for i,v in ipairs(humanoid:GetChildren()) do
		if v.Name=="creator" then
			v:Destroy()
		end
	end
	local creator_tag=Instance.new("ObjectValue");
	creator_tag.Value=player;
	creator_tag.Name="creator";
	creator_tag.Parent=humanoid;
	c.debris:AddItem(creator_tag,1);
end
function c.blow(hit)
	if hit.Parent~=nil then
		local humanoid=hit.Parent:findFirstChild("Humanoid");
		local vCharacter=c.gear.Parent;
		if vCharacter~=nil then
			local vPlayer=game.Players:playerFromCharacter(vCharacter);
			if vPlayer~=nil then
				local hum=vCharacter:findFirstChild("Humanoid");
				if humanoid~=nil then
					if hum~=nil and humanoid~=hum and hum.Health>0 then
						c.tagHumanoid(humanoid,vPlayer);
						local t=hit.Parent:FindFirstChild("Torso");
						if t~=nil then
							humanoid:TakeDamage(c.c.damage);
						end
					end
				end
			end
		end
	end
end
c.gear.Enabled=true;
function c.onActivated()
	if c.gear.Enabled and not c.taunting.Value then
		c.gear.Enabled=false
		local character=c.gear.Parent;
		local humanoid=character.Humanoid;
		if humanoid==nil then
			print("Humanoid not found");
			return;
		end
		c.sound:Play();
		newanim=c.anims[math.random(1,#c.anims)]
		while newanim==c.gear.RunAnim.Value do
			newanim=c.anims[math.random(1,#c.anims)];
		end
		c.gear.RunAnim.Value=newanim;
		if newanim=="OverHeadSwing" then
			c.c.damage=c.c.swingdamage;
		else
			c.c.damage=c.c.slashdamage;
		end
		wait(c.c.swingrate)
		c.c.damage=c.c.basedamage;
		c.gear.Enabled=true;
	end
end
c.gear.Activated:connect(c.onActivated);
c.sword.Touched:connect(c.blow);

--[[ alexnewtron 2014 ]]--
script.Parent:WaitForChild("Handle");
print(1);
local gearConfig = {
	gear = script.Parent,
	equipped = false,
	isSwinging = false,
	attackDeb = true,
	check = true,
	taunting = false,
	debris = game:GetService("Debris"),
	taunting = script.Parent:WaitForChild("Taunting"),
	runAnim = script.Parent:WaitForChild("RunAnim"),
	specialSound = script.Parent.Handle:WaitForChild("dragon"),
	specialScript = script:WaitForChild("fire"),
	lastHumanoid = nil,
	anims = {
		script.Parent:WaitForChild("TonightsForcast")
	},
	sounds = {
		script.Parent.Handle:WaitForChild("Slash1")
	},
	readyAnims = {},
	deb = 0.75,
	damage = 25
};
gearConfig.runAnim.Value = "";
gearConfig.taunting.Value = false;
gearConfig.gear.Equipped:connect(function(m)
	gearConfig.equipped = true;
	if (m ~= nil) then
		local human=script.Parent.Parent:findFirstChild("Humanoid");
		gearConfig.lastHumanoid = human;
		for i=1, #gearConfig.anims do
			table.insert(gearConfig.readyAnims, human:LoadAnimation(gearConfig.anims[i]));		
		end		
		m.KeyDown:connect(function(key)
			local key = string.lower(key);
			local leftArm =script.Parent.Parent:findFirstChild("Left Arm");
			local torso = script.Parent.Parent:findFirstChild("Torso");
			local special = leftArm:findFirstChild("Effect");
			if (key == "l" or key == "t" or key == "g" or key == "e" or key == "q") then
				if (gearConfig.check and not gearConfig.taunting.Value and human ~= nil and human.Health > 0 and torso ~= nil and leftArm ~= nil and special == nil) then
					Spawn(function() wait(4); human.WalkSpeed = 16; end);
					gearConfig.check = false;
					gearConfig.taunting.Value = true;
					human.WalkSpeed=7;
					gearConfig.readyAnims[1]:Play();
					gearConfig.specialSound:Play();					
					local p=Instance.new("Part");
					p.Name="Effect";
					p.CanCollide=false;
					p.Transparency=1;
					p.FormFactor="Custom";
					p.Size=Vector3.new(0,0,0);
					local frost=Instance.new("Fire");
					frost.Color=Color3.new(1,.49,0);
					frost.Size=8;
					frost.Heat=25;
					frost.Parent=p;
					local w=Instance.new("Motor");
					w.Part0=leftArm;
					w.Part1=p;
					w.C0=CFrame.new(0,0,0)*CFrame.Angles(math.pi,0,0);
					w.Parent=p;
					gearConfig.debris:AddItem(p,10);
					p.Parent=leftArm;
					wait(.7);
					for i=1,25 do
						if p and p.Parent~=nil then
							local p2=Instance.new("Part");
							p2.Name="Effect";
							p2.Transparency=1;
							p2.BrickColor=BrickColor.new("Bright red");
							p2.CanCollide=false;
							p2.FormFactor="Custom";
							p2.Position=p.Position;
							p2.Shape="Ball";
							p2.Size=Vector3.new(3,3,3);
							p2.Velocity=(torso.CFrame.lookVector*40)+(Vector3.new((math.random()-.5)*2,(math.random()-.5)*2,(math.random()-.5)*2)*8);
							local f2 = Instance.new("Fire");
							f2.Color=Color3.new(1,.49,0);
							f2.Size = 5;
							f2.Heat = 10;
							f2.Parent = p2;
							local bf=Instance.new("BodyForce");
							bf.force=Vector3.new(0,p2:GetMass()*196.2,0);
							bf.Parent=p2;
							local sc=gearConfig.specialScript:clone();
							sc.Parent=p2;
							sc.Disabled=false;
							local c=Instance.new("ObjectValue");
							c.Name="creator";
							c.Value=game.Players.LocalPlayer;
							c.Parent=p2;
							gearConfig.debris:AddItem(p2,1);
							p2.Parent=workspace;
						end
						wait(.1);
					end
					wait(1.5);
					if frost then
						frost.Enabled = false;
					end	
					gearConfig.taunting.Value = false;
					wait(10);
					gearConfig.check = true;
				end		
			end
		end);
	end
end);
gearConfig.gear.Unequipped:connect(function()
	gearConfig.equipped = false;
	if (gearConfig.lastHumanoid ~= nil) then
		gearConfig.lastHumanoid.WalkSpeed = 16;
	end
end);
gearConfig.runAnim.Changed:connect(function()
	local h=gearConfig.gear.Parent:FindFirstChild("Humanoid")
	local t=gearConfig.gear.Parent:FindFirstChild("Torso")
	local anim=gearConfig.gear:FindFirstChild(gearConfig.runAnim.Value);
	if anim and t and h then
		local theanim=h:LoadAnimation(anim);
		if theanim and h.Health>0 then
			theanim:Play()
		end
	end
end);

--[[ alexnewtron 2014 ]]--
local config = {
	object = script.Parent,
	debris=game:GetService("Debris"),
	creator = script.Parent:WaitForChild("creator"),
};
config.object.Touched:connect(function(hit)
	if hit and hit~=nil and hit.Parent~=nil then
		local h=hit.Parent:FindFirstChild("Humanoid");
		local t=hit.Parent:FindFirstChild("Torso");
		if h~=nil and t~=nil then

			if config.creator.Value and config.creator.Value~=nil and t:FindFirstChild("FireWeld") then
				local plr=game.Players:GetPlayerFromCharacter(hit.Parent);
				if plr then
					if plr==config.creator.Value then
						return;
					end
				end
				h:TakeDamage(5);
			elseif (config.creator.Value and config.creator.Value~=nil and t:FindFirstChild("FireWeld") == nil) then
				local plr=game.Players:GetPlayerFromCharacter(hit.Parent);
				if plr then
					if plr==config.creator.Value then
						return;
					end
				end
				local d = script.death:Clone();
				d.creator.Value = config.creator.Value;
				d.Parent = t;
				d.Disabled = false;
				local p=Instance.new("Part");
				p.Name="Effect";
				p.BrickColor=BrickColor.new("Pastel light blue");
				p.FormFactor="Custom";
				p.Size=Vector3.new(2,2,2);
				p.Transparency=1;
				p.CanCollide=false;
				p.TopSurface="Smooth";
				p.BottomSurface="Smooth";
				local f = Instance.new("Fire");
				f.Color=Color3.new(255/255,127/255,0);
				f.Size=15;
				f.Heat=25;
				f.Parent=p;
				local cf=CFrame.Angles(0,math.pi*2*math.random(),.5*math.random());
				p.CFrame=t.CFrame*cf;
				local w=Instance.new("Motor");
				w.Name="FireWeld";
				w.Part0=t;
				w.Part1=p;
				w.C0=CFrame.Angles(0,math.pi*2*math.random(),.5*math.random());
				w.Parent=t;
				config.debris:AddItem(p,8);
				p.Parent=workspace;
			--	h.WalkSpeed=5;
				delay(0,function()
					wait(10+math.random())
					if h and h~=nil then
				--		h.WalkSpeed=16;
					end
					if p and p~=nil then
						p:Destroy();
					end
				end)
				config.object:Destroy();
			end
		end
	end
end)
wait(.8);
config.object:Destroy();

--[[ alexnewtron 2014 ]]--
local c = {
	p = game.Players.LocalPlayer,
	h = game.Players.LocalPlayer.Character:findFirstChild("Humanoid"),
	debris=game:GetService("Debris"),
	c = script:WaitForChild("creator")
};
function c.r()
	for i,v in ipairs(c.h:GetChildren()) do
		if v.Name=="creator" then
			v:Destroy()
		end
	end	
	local creator_tag=Instance.new("ObjectValue");
	creator_tag.Value=c.c.Value;
	creator_tag.Name="creator";
	creator_tag.Parent=c.h;
end
if (c.h ~= nil) then
	for i=1, 5 do
		c.r();
		c.h:TakeDamage(13);
		wait(1.5);
	end	
end
script:Destroy();
