Properties.ProtectedString:
--[[ alexnewtron 2014 ]]--

local ClientAgent = require(script:WaitForChild("ClientAgent"));
local Controller = script:WaitForChild("Controller");
local Gear = script.Parent;

local PlayersService = Game:GetService('Players');
local DebrisService = Game:GetService('Debris');

local Character = nil;
local Player = nil;
local Humanoid = nil;

Gear:WaitForChild("Handle");

local d = { Debris = game:GetService("Debris") };
local s = { 
	["Equip"] = Gear.Handle:WaitForChild("EquipSound"),
	["Slash1"] = Gear.Handle:WaitForChild("SlashSound"),
	["SwordHit"] = Gear.Handle:WaitForChild("SwordHit")
 };

function TagHumanoid(a,b)local c=Instance.new("ObjectValue")c.Name="creator"c.Value=b;d.Debris:AddItem(c,2)c.Parent=a end
function UntagHumanoid(a)for b,c in pairs(a:GetChildren())do if c:IsA("ObjectValue")and c.Name=="creator"then c:Destroy()end end end

function Controller.OnServerInvoke(p, e, ...)
	local tuples={...};
	if (e == ClientAgent.SERVER_START) then
		d.throw();
	elseif (e == ClientAgent.SERVER_PLAY_SOUND) then
		s[tuples[1]]:Play();
	elseif (e == ClientAgent.DAMAGE_PLAYER) then
		UntagHumanoid(tuples[1]);
		TagHumanoid(tuples[1],Player);
		tuples[1]:TakeDamage(tuples[2]);
		s["SwordHit"]:Play();
		if (tuples[1].Health <= 0) then
			local p = tuples[1].Parent:GetChildren();
			for i=1, #p do
				if (p[i]:IsA("Part")) then
					p[i].BrickColor = BrickColor.new("Cyan");
					p[i].Material = Enum.Material.Foil;
					local s = Instance.new("Sparkles", p[i]);
				elseif (p[i]:IsA("Shirt") or p[i]:IsA("Pants") or p[i]:IsA("CharacterMesh")) then
					p[i]:Destroy();
				end
			end
		end
	end
end;


Gear.Equipped:connect(function()
	Character = Gear.Parent;
	Player = game:GetService("Players"):GetPlayerFromCharacter(Character);
	Humanoid = Character:FindFirstChild("Humanoid");
	if not Character or not Humanoid or Humanoid.Health == 0 then return; end
	Controller:InvokeClient(Player, ClientAgent.REQUEST_ANIMATIONS, "Equip");
	s["Equip"]:Play();
	Gear.Activated:connect(function()
		if not Gear.Enabled or not Humanoid or Humanoid.Health == 0 then return; end
		Gear.Enabled = false;
		s["Slash1"]:Play();
		Controller:InvokeClient(Player, ClientAgent.REQUEST_ANIMATIONS, "SlashAnim");
		Spawn(function()
			while not Gear.Enabled do
				local c = Gear.Handle:Clone();
				c.Anchored = true;
				c.CanCollide = false;
				c.Transparency = .5;
				c.CFrame = Gear.Handle.CFrame;
				d.Debris:AddItem(c, 3);
				c.Parent = workspace;				
				wait();
			end
		end)
		wait(.5);
		Gear.Enabled = true;
	end)
end);

Gear.Unequipped:connect(function()
	swing = false;
	Gear.Enabled = true;
	Controller:InvokeClient(Player, ClientAgent.REQUEST_ANIMATIONS_END);
	local leftArm = Character:findFirstChild("Left Arm");
	if (leftArm:findFirstChild("Shield")) then
		leftArm["Shield"]:Destroy();
	end
end)

--[[ alexnewtron 2014 ]]--


local Gear = script.Parent.Parent;
local Player = nil;
local Character = nil;
local Humanoid = nil;
local Controller = script.Parent:WaitForChild("Controller");



local d = {
	Debris = game:GetService("Debris")
};

local ClientAgent = require(script.Parent:WaitForChild("ClientAgent"));
local preparedAnimations = {
	Gear:WaitForChild("Equip"),
	Gear:WaitForChild("SlashAnim"),
};
local readyAnimations = {};


Controller.OnClientInvoke = (function(...)
	local tuples={...};
	if (tuples[1] == ClientAgent.REQUEST_TARGETPOINT) then
		return ((Humanoid and Humanoid.TargetPoint) or nil);
	elseif (tuples[1] == ClientAgent.REQUEST_ANIMATIONS) then
		repeat wait() until readyAnimations[tuples[2]] ~= nil
		readyAnimations[tuples[2]]:Play();
		return true;
	elseif (tuples[1] == ClientAgent.REQUEST_ANIMATIONS_END) then
		for i, v in pairs(readyAnimations) do readyAnimations[i]:Stop(); end
		return true;
	end
end);

Gear.Equipped:connect(function()
	Character = Gear.Parent;
	Player = game.Players.LocalPlayer;
	Character = Player.Character;
	Humanoid = Character:findFirstChild("Humanoid");
	if not Character or not Humanoid or Humanoid.Health == 0 then return; end
	if (#readyAnimations == 0) then
		for i=1, #preparedAnimations do
			readyAnimations[preparedAnimations[i].Name] = Humanoid:LoadAnimation(preparedAnimations[i]);
		end
	end
end);

Gear.Handle.Touched:connect(function(p)
	if (Player ~= nil and Character ~= nil and p ~= nil and p.Parent ~= nil and not Gear.Enabled and p.Parent ~= Character) then
		local h = p.Parent:findFirstChild("Humanoid");
		local t = p.Parent:findFirstChild("Torso");
		if (h ~= nil and t ~= nil and h.Health > 0) then
			local cT = h:findFirstChild("creator");
			if (cT == nil or cT ~= nil and cT.Value ~= Player) then
				Controller:InvokeServer(ClientAgent.DAMAGE_PLAYER, h, 40);
			end
		end
	end
end);

-- REMOTE FUNCTION CLIENT AGENT 8-10-14 ALEXNEWTRON
return {
	REQUEST_TARGETPOINT = 1,
	REQUEST_ANIMATIONS = 2,
	REQUEST_PLAYER = 3,
	REQUEST_ANIMATIONS_END = 4,
	SERVER_PLAY_SOUND = 6,
	DAMAGE_PLAYER = 7
};

--[[ alexnewtron 2014 ]]--

local c = script.Parent;

c.Anchored = false;

local bP = Instance.new("BodyPosition", c);
bP.maxForce = Vector3.new(math.huge, math.huge, math.huge);
bP.position = c.Position;


while ((bP.position - c.Position).magnitude > 2) do
	bP.position = c.Position;
	wait();
end

c:Destroy();
