--[[ alexnewtron 2014 ]]--

local ClientAgent = require(script:WaitForChild("ClientAgent"));
local Controller = script:WaitForChild("Controller");
local Gear = script.Parent;

local Character = nil;
local Player = nil;
local Humanoid = nil;

local Check = true;

local d = { Debris = game:GetService("Debris") };
local s = {};

function TagHumanoid(a,b)local c=Instance.new("ObjectValue")c.Name="creator"c.Value=b;d.Debris:AddItem(c,2)c.Parent=a end
function UntagHumanoid(a)for b,c in pairs(a:GetChildren())do if c:IsA("ObjectValue")and c.Name=="creator"then c:Destroy()end end end

function d.throw()
	local bomb = script.Parent.Handle:Clone()
	bomb.CanCollide = true
	bomb.Transparency = 0
	bomb.Position = script.Parent.Handle.Position;
	bomb.Name = "TrickOrTrap"
	bomb.Locked = true;
	local gyro = Instance.new("BodyGyro", bomb);
	gyro.maxTorque = Vector3.new(400000, 400000, 400000);
	gyro.P = 3000;
	gyro.D = 500;	
	local owner = Instance.new("ObjectValue", bomb);
	owner.Name = "creator";
	owner.Value = game.Players.LocalPlayer;
	local new_script = script.TrickOrTreat:clone()
	new_script.Disabled = false
	new_script.Parent = bomb	
	bomb.Parent = workspace
	Gear:Destroy();
end

function Controller.OnServerInvoke(p, e, ...)
	local tuples={...};
	if (e == ClientAgent.SERVER_START) then
		d.throw();
	elseif (e == ClientAgent.SERVER_PLAY_SOUND) then
		s[tuples[1]]:Play();
	end
end;

Gear.Equipped:connect(function()
	Character = Gear.Parent;
	Player = game:GetService("Players"):GetPlayerFromCharacter(Character);
	Humanoid = Character:FindFirstChild("Humanoid");
	if not Character or not Humanoid or Humanoid.Health == 0 then return; end
end);

Gear.Activated:connect(function()
	if not Check or not Humanoid or Humanoid.Health == 0 then return; end
	Check = false;
	Controller:InvokeClient(Player, ClientAgent.REQUEST_ANIMATIONS, "Prepare");
end)

Gear.Unequipped:connect(function()
	Character = nil;
	Player = nil;
	Humanoid = nil;
end);

--[[ alexnewtron 2014 ]]--


local Gear = script.Parent.Parent;
local Player = nil;
local Character = nil;
local Humanoid = nil;
local Controller = script.Parent:WaitForChild("Controller");

local d = {
	Debris = game:GetService("Debris")
};

local ClientAgent = require(script.Parent:WaitForChild("ClientAgent"));
local preparedAnimations = {
	Gear:WaitForChild("Prepare")
};
local readyAnimations = {};


Controller.OnClientInvoke = (function(...)
	local tuples={...};
	if (tuples[1] == ClientAgent.REQUEST_TARGETPOINT) then
		return ((Humanoid and Humanoid.TargetPoint) or nil);
	elseif (tuples[1] == ClientAgent.REQUEST_ANIMATIONS) then
		readyAnimations[tuples[2]]:Play();
		return true;
	elseif (tuples[1] == ClientAgent.REQUEST_ANIMATIONS_END) then
		for i, v in pairs(readyAnimations) do readyAnimations[i]:Stop(); end
		return true;
	end
end);

Gear.Equipped:connect(function()
	Character = Gear.Parent;
	Player = game.Players.LocalPlayer;
	Character = Player.Character;
	Humanoid = Character:findFirstChild("Humanoid");
	if not Character or not Humanoid or Humanoid.Health == 0 then return; end
	if (#readyAnimations == 0) then
		for i=1, #preparedAnimations do
			readyAnimations[preparedAnimations[i].Name] = Humanoid:LoadAnimation(preparedAnimations[i]);
		end
		readyAnimations["Prepare"].KeyframeReached:connect(function(k)
			if (k == "throw") then
				Controller:InvokeServer(ClientAgent.SERVER_START);
			end
		end);
	end
end);

-- CLIENT AGENT --
return {
	REQUEST_TARGETPOINT = 1,
	REQUEST_ANIMATIONS = 2,
	REQUEST_PLAYER = 3,
	REQUEST_ANIMATIONS_END = 4,
	SERVER_START = 5,
	SERVER_PLAY_SOUND = 6,
};

--[[ alexnewtron 2014 ]]--

local Pumpkin = script.Parent;
local Creator = Pumpkin:WaitForChild("creator");

local tween = require(script:WaitForChild("b"));
local easing = require(script:WaitForChild("e"));

local Deb = true;

local Debris = game:GetService("Debris");

function UntagHumanoid(a)for b,c in pairs(a:GetChildren())do if c:IsA("ObjectValue")and c.Name=="creator"then c:Destroy()end end end

local Treat = game:GetService("InsertService"):LoadAsset(39258329):GetChildren()[1];

local Sounds = {
	["ToT"] = Pumpkin:WaitForChild("TrickOrTreat"),
	["Trick"] = Pumpkin:WaitForChild("Trick"),
	["Treat"] = Pumpkin:WaitForChild("Treat"),
	["Splat"] = Pumpkin:WaitForChild("Splat"),
	["Victory"] = Pumpkin:WaitForChild("Victory"),
	["Come1"] = Pumpkin:WaitForChild("Come1"),
	["Come2"] = Pumpkin:WaitForChild("Come2")
}

local ts = tween("forward");
ts.add(1, {pX = 1.2, pZ = 1.35, pY = 1.2, m = .7}, easing.outBack);
ts.add(1, {pX = 2.2, pZ = 2.35, pY = 2, m = 1.2}, easing.outBack);
for x=1, 40 do
	local p = ts.getCurrentProperties();
--	Pumpkin.Size = Vector3.new(p.pX, p.pZ, p.pY);
	Pumpkin.Mesh.Scale = Vector3.new(p.m, p.m, p.m);	
	ts.update(0.025); wait(0.025);
end

Pumpkin.Anchored = true;
Sounds["ToT"]:Play();
Debris:AddItem(Pumpkin, 120);

spawn(function()
	while Deb do
		wait(math.random(5,10));
		local a = math.random(1,2);
		if Deb then
			Sounds["Come"..a]:Play();
		end
	end
end);

Pumpkin.Touched:connect(function(hit)
	if (Deb and hit and hit ~= nil and hit.Parent and hit.Parent ~= nil) then
		local h = hit.Parent:findFirstChild("Humanoid");
		local t = hit.Parent:findFirstChild("Torso");
		if (h ~= nil and t ~= nil and h.Health > 0) then
			local r = math.random(1,2);
			Deb = false;
			if (r == 1) then -- TRICK
				h.WalkSpeed = 1;
				Sounds["Trick"]:Play();
				wait(2.5);
				Sounds["Splat"]:Play();
				Pumpkin.Transparency = 1;
				local e = Instance.new("Explosion", script.Parent);
				e.ExplosionType = Enum.ExplosionType.NoCraters;
				e.BlastPressure = 0;
				e.BlastRadius = 500;
				e.Position = script.Parent.Position;
				UntagHumanoid(h);
				Creator.Parent = h;
				h:TakeDamage(20);
				h.WalkSpeed = 16;
				Debris:AddItem(Pumpkin, 2);
			elseif (r == 2) then -- TREAT
				h.WalkSpeed = 1;
				Sounds["Treat"]:Play();
				wait(2.5);
				Sounds["Victory"]:Play();
				Pumpkin.Transparency = 1;				
				h.Health = h.Health + math.random(10,25);				
				local p1 = Treat:Clone();
				p1.Parent = t.Parent;
				h.WalkSpeed = 16;
				Debris:AddItem(Pumpkin, 2);
			end
		end
	end
end);
