Properties.string:
local Tool = script.Parent;
function UpdateIcon(mouse) if (mouse == nil) then return end if (Tool.Enabled == true) then mouse.Icon = "rbxasset://textures\\GunCursor.png" else mouse.Icon = "rbxasset://textures\\GunWaitCursor.png" end end
function onButton1Down(mouse) UpdateIcon(mouse) end
function onEquippedLocal(mouse)
UpdateIcon(mouse) end
Tool.Equipped:connect(onEquippedLocal)
